INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("West Side Market", "Culture", "Cleveland's oldest public market with over 100 vendors offering food, drink and groceries.", "1979 W 25th St, Cleveland, OH 44113", "img/wsm.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Lakeview Cemetery", "Culture", "A peaceful, sprawling and historical burial ground adorned with the Wade Chapel, Garfield Monument and home of graves to a few historical figures.", "12316 Euclid Avenue, Cleveland, OH 44106", "img/lakeview.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("A Christmas Story House", "Culture", "The house featured in the movie, A Christmas Story.", "3159 W 11th St, Cleveland, OH 44109", "img/christmashouse.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Free Stamp", "Culture", "The world's largest rubber stamp.", "East 9th Street and Lakeside Avenue, Cleveland, OH 44114", "img/freestamp.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Dunham Tavern", "Culture", "The oldest building in Cleveland.", "6709 Euclid Ave, Cleveland, OH 44103", "img/dunham.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Rock'n'Roll Hall of Fame", "Culture", "Cleveland is the home of rock'n'roll, so naturally the hall of fame would be here!", "1100 E 9th St, Cleveland, OH 44114", "img/rockhall.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Great Lakes Brewing Company", "Nightlife", "Brewing up yearly concoctions like its Christmas Ale and Conway's, and getting you drunk the Cleveland way.", "2516 Market Ave. Cleveland Ohio 44113", "img/greatlakes.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Cedar Lee Theatre", "Culture", "A place to see offbeat indie films.", "2163 Lee Rd, Cleveland Heights, OH 44118", "img/cedarlee.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Tina's Nite Club", "Nightlife", "Karaoke and drinks in a wonderfully divey atmosphere.", "5400 Herman Ave, Cleveland, OH 44102", "img/tinas.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Beachland Ballroom", "Nightlife", "A place to catch the latest band in town, or just to have a drink.", "15711 Waterloo Rd, Cleveland, OH 44110", "img/beachland.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Severance Hall", "Culture", "Home of the world-renowned Cleveland Orchestra and featuring some of the most beautiful architecture in Cleveland.", "11001 Euclid Ave, Cleveland, OH 44106", "img/severance.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Cleveland Museum of Art", "Culture", "A crown jewel in University Circle and featuring art of all types from all over the world.", "11150 East Blvd, Cleveland, OH 44106", "img/cma.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Cleveland Museum of Natural History", "Culture", "Explore the natural history of the world through dinosaur bones, a planetarium, a wildlife exhibit exhibit and more.", "1 Wade Oval Dr, Cleveland, OH 44106", "img/cmnh.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Cleveland Metroparks Zoo", "Culture", "Explore 18 reservations spanning more than 23,000 acres with more than 300 miles of trails, eight golf courses, eight lakefront parks and a nationally-acclaimed zoo.", "3900 Wildlife Way, Cleveland, OH 44109", "img/zoo.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("78th Street Studios", "Culture", "The largest fine arts complex in Northeast Ohio with over 60 galleries and studios.", "1300 W 78th St, Cleveland, OH 44102", "img/78street.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Helio Terra", "Restaurants", "Vegan cafe focusing on nutritional density and great flavors, specializing in raw vegan, gluten free, oil free food, cold pressed juice and smoothies.", "619 Prospect Ave E, Cleveland, OH 44115", "img/helio.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Nature Center at Shaker Lake", "Culture", "The Nature Center at Shaker Lakes conserves a natural area, connects people with nature and inspires environmental stewardship.", "2600 S Park Blvd, Cleveland, OH 44120", "img/naturecenter.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Happy Dog", "Nightlife", "The Happy Dog is a neighborhood corner bar in the best sense of the term. We've got hot dogs and beer, and lots of live music, from rock and country to punk and polka. The bartenders are friendly and the bar itself is open and inviting - a racetrack oval wood bar, with plenty of old chrome barstools. It's not retro because it's pretty much unchanged from the time the Socotch family built the place back in the late 40's.", "5801 Detroit Rd, Cleveland, OH 44102", "img/happydog.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("The Sidequest", "Nightlife", "The Side Quest is place for everyone, no matter how nerdy, to enjoy the things they love while having a few great craft beers with friends. With an extensive selection of ciders, meads and geeky cocktails, there is something for everyone.", "17900 Detroit Avenue, Lakewood, Ohio, 44107", "img/sidequest.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Brasa Grill", "Restaurants", "Brasa Grill Steakhouse is Cleveland's first Churrascaria (choo-rah-scah-REE-ah). A Churrascaria is a restaurant that serves meat, mostly grilled, and Rodizio is a method of serving the different cuts of meat that originated in Southern Brazil in the 1800's. The concept is to serve you a wide variety of different cuts of beef, pork, lamb, chicken, etc. in sucession to each diner individually right at your table.", "1300 West 9th Street, Cleveland, Ohio 44114", "img/brasa.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Lakewood Dog Park", "Culture", "We provide a leash free area for dogs to play and socialize. There is a small dog area on the west end of the park for toy breeds that do not play well with larger dogs. Additionally, Friends of the Lakewood Dog Park, Inc., plans events for members and non-members, including year-round activities for dog owners and their pets, social events, fitness activities and participation in civic events.", "1699 Valley Parkway, Lakewood, Ohio 44107", "img/dogpark.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("Tommy's", "Restaurants", "Vegan friendly restaurant in the center of Coventry. Tommy's has the best milkshakes in Cleveland.", "1824 Coventry Rd, Cleveland Heights 44118", "img/tommys.jpg");
INSERT INTO locations (locationName, category, description, address, picture_url) VALUES ("16-Bit Bar + Arcade", "Nightlife", "Retro themed bar with Classic Arcade Games and great cocktails.", "15012 Detroit Ave, Lakewood, 44107", "img/16bit.jpg");
